        filename = file_arr[len(file_arr) - 1]
        filename = unquote(filename, 'utf-8')
        print('filename:', filename)

        f = open('ian595/%s' % filename, 'w', encoding='utf-8')
        f.write(html)
        f.flush()
        f.close()
        print('sub%d end...' % self.sub_index)
        self.sub_index += 1

